            </Card.Body>
          </Card>

          <Card>
            <Card.Body>
              
              <img src={car.image}/>
              <h1>{car.name}</h1>
              <p>{car.category}</p>
              <h2>{car.price}</h2>
            </Card.Body>
          </Card>
         </div>
       
        <Footer/>
       </div> 
    )
}

