import axios from "axios";
import { useEffect, useState, item } from "react";
import Footer from "../../component/Footer";
import Navbar from "../../component/Navbar";
import Banner from "../../component/Benner";
import DetailCar from "../DetailCar";
import { Link } from "react-router-dom";
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import {Col, Row, Button, Form} from "react-bootstrap";



const Cars = () => {

    const [data,setdata]=useState([]);
    const [name,setName]=useState("");
  
    
    // console.log('data ini adalah',data);

    useEffect(() => {
        axios
          .get("https://bootcamp-rent-car.herokuapp.com/admin/car")
          .then((res) => setdata(res.data))

          .catch((err) => console.log(err));
      }, []);
    //   console.log("data ini namee", name)

      const handleChangeName = (e) => {
        setName(e.target.value)
      }
      const handleSearch =()=>{
         const newArr = data.filter(data => data.name === name);
          setdata(newArr)
      }
    

    return (
       <div>
        <Navbar/>
        <Banner/>
        <Row>
        {
            !!data.length && (data.map((item)=> (
              <div class="card" style="width: 18rem;">
                <img src={item.image} class="card-img-top" />
                   <div class="card-body">
                       <h5 class="card-title">Card title</h5>
                       <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        <a href="#" class="btn btn-primary">Go somewhere</a>
                  </div>
                </div>



              
                    // <Col>
                    //     <div>
                    //         <img src={item.image}/>
                    //     </div>
                    //     <div>
                    //         <h1>{item.name}</h1>
                    //         <p>{item.price}</p>
                    //     </div>
                    //     <Link to={`/detailmobil/${item.id}`}>
                    //     <button>pilih Mobil</button>
                    //     </Link>
                    //  </Col>   
                ))
            )
        }
        </Row>
        <Footer/>
      </div>
        
    )
    
}

export default Cars;
