import axios from "axios";
import { useEffect, useState } from "react";
import Footer from "../../component/Footer";
import Navbar from "../../component/Navbar";
import DetailCar from "../DetailCar";
import { Link } from "react-router-dom";


import {Button} from 'react-bootstrap';
import {Form} from 'react-bootstrap';

const Cars = () => {

    const [data,setdata]=useState([]);
    const [name,setName]=useState("");
  
    
    // console.log('data ini adalah',data);

    useEffect(() => {
        axios
          .get("https://bootcamp-rent-car.herokuapp.com/admin/car")
          .then((res) => setdata(res.data))

          .catch((err) => console.log(err));
      }, []);
    //   console.log("data ini namee", name)

      const handleChangeName = (e) => {
        setName(e.target.value)
      }

      const handleSearch =()=>{
         const newArr = data.filter(data => data.name === name);
          setdata(newArr)
         
       
      }
    

    return (

      
       <div>
        <Navbar/>
        
        <div className="w-full h-screen bg-[#f1f3ff] flex flex-col justify-between  " >
        <div className="grid md:grid-cols-2 max-w-[1240px] m-auto">
          <div className="flex justify-content-center" >
          
          <Form.Control
        type="text"
        placeholder="Disabled input"
        aria-label="Disabled input example"
        disabled
        readOnly
      />
      <br />
      <Form.Control
        type="text"
        placeholder="Disabled readonly input"
        aria-label="Disabled input example"
        readOnly
      />


          </div>
         
        </div>
      </div>

        
        
      
       


        
        
        {
            !!data.length && (
                data.map((item)=> (
                    <div>
                        <div>
                            <img src={item.image}/>
                        </div>
                        <div>
                            <h1>{item.name}</h1>
                            <p>{item.price}</p>
                        </div>
                        
                        <Link to={`/detailmobil/${item.id}`}>
                        <button>pilih Mobil</button>
                        </Link>
                        </div>

                        

                ))
            )
        }
        <Footer/>
       </div> 
    )
}

export default Cars;
