import axios from "axios";
import { useEffect, useState } from "react";
import Footer from "../../component/Footer";
import Navbar from "../../component/Navbar";
import DetailCar from "../DetailCar";
import { Link } from "react-router-dom";


import {Button} from 'react-bootstrap';
import {Form} from 'react-bootstrap';

const Cars = () => {

    const [data,setdata]=useState([]);
    const [name,setName]=useState("");
  
    
    // console.log('data ini adalah',data);

    useEffect(() => {
        axios
          .get("https://bootcamp-rent-car.herokuapp.com/admin/car")
          .then((res) => setdata(res.data))

          .catch((err) => console.log(err));
      }, []);
    //   console.log("data ini namee", name)

      const handleChangeName = (e) => {
        setName(e.target.value)
      }

      const handleSearch =()=>{
         const newArr = data.filter(data => data.name === name);
          setdata(newArr)
         
       
      }
    

    return (

      
       <div>

        <div>
        <Navbar/>
        </div>

        <div>
        <Form className=" flex">
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Nama Mobil</Form.Label>
        <Form.Control  placeholder="Ketik Nama Mobil" onChange={(e)=>handleChangeName(e)}/>
        
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Kategori</Form.Label>
        
        <Form.Select >
          
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Harga</Form.Label>
        <Form.Select >
        
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Status</Form.Label>
        <Form.Control type="password" placeholder="Password" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicCheckbox">
        <Form.Check type="checkbox" label="Check me out" />
      </Form.Group>
      
      <Button variant="primary"  onClick={handleSearch}>
        Search
      </Button>
    </Form>






        
        
        {
            !!data.length && (
                data.map((item)=> (
                    <div>
                        <div>
                            <img src={item.image}/>
                        </div>
                        <div>
                            <h1>{item.name}</h1>
                            <p>{item.price}</p>
                        </div>
                        
                        <Link to={`/detailmobil/${item.id}`}>
                        <button>pilih Mobil</button>
                        </Link>
                        </div>

                        

                ))
            )
        }
        <Footer/>
        </div>

        
        
      
       
       </div> 
    )
}

export default Cars;
