import axios from "axios";
import { useEffect, useState } from "react";
import Footer from "../../component/Footer";
import Navbar from "../../component/Navbar";
import DetailCar from "../DetailCar";
import { Link } from "react-router-dom";
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';


import {Button} from 'react-bootstrap';
import {Form} from 'react-bootstrap';

const Cars = () => {

    const [data,setdata]=useState([]);
    const [name,setName]=useState("");
  
    
    // console.log('data ini adalah',data);

    useEffect(() => {
        axios
          .get("https://bootcamp-rent-car.herokuapp.com/admin/car")
          .then((res) => setdata(res.data))

          .catch((err) => console.log(err));
      }, []);
    //   console.log("data ini namee", name)

      const handleChangeName = (e) => {
        setName(e.target.value)
      }

      const handleSearch =()=>{
         const newArr = data.filter(data => data.name === name);
          setdata(newArr)
         
       
      }
    

    return (

      
       <div>
        <Navbar/>
        
        <div className="w-full h-screen bg-[#f1f3ff] flex flex-col justify-between  " >
        <div className="grid md:grid-cols-2 max-w-[1240px] m-auto">
          <div >
          <Form className="flex justify-content-md-center">
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Nama Mobil</Form.Label>
        <Form.Control  placeholder="Ketik Nama Mobil" onChange={(e)=>handleChangeName(e)}/>
        
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Kategori</Form.Label>
        
        <Form.Select >
          
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3">
        <Form.Label>Harga</Form.Label>
        <Form.Select >
        
        </Form.Select>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Status</Form.Label>
        <Form.Control type="password" placeholder="Password" />
      </Form.Group>
      
      
      <Button variant="primary"  onClick={handleSearch}>
        Search
      </Button>
    </Form>





          </div>
         
        </div>
      </div>

      <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="holder.js/100px180?text=Image cap" />
      <Card.Body>
        <Card.Title>Card Title</Card.Title>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
      </Card.Body>
      
      <Card.Body>
        <Card.Link href="#">Card Link</Card.Link>
        <Card.Link href="#">Another Link</Card.Link>
      </Card.Body>
    </Card>

        
        
      
       


        
        
      
       

                data.map((item)=> (
                    <div>
                        <div>
                            <img src={item.image}/>
                        </div>
                        <div>
                            <h1>{item.name}</h1>
                            <p>{item.price}</p>
                        </div>
                        
                        <Link to={`/detailmobil/${item.id}`}>
                        <button>pilih Mobil</button>
                        </Link>
                        </div>

                        

                ))
            )
        }
        <Footer/>
       </div> 
    )
}

export default Cars;
